mapper.select:
/*CommonHelpMapper.HELP_DEPT*/ SELECT A.COMPANY_CD AS "COMPANY_CD" , A.DEPT_CD AS "DEPT_CD" , A.DEPT_NM AS "DEPT_NM" , A.DEPT_PARENT AS "DEPT_PARENT" , A.DEPT_LEVEL AS "DEPT_LEVEL" FROM ES_DEPT A WHERE A.COMPANY_CD = #{COMPANY_CD}
AND ( A.DEPT_CD LIKE '%' || #{KEYWORD} || '%' OR A.DEPT_NM LIKE '%' || #{KEYWORD} || '%' )
AND ( A.DEPT_CD = #{LOGIN_DEPT_CD} OR A.DEPT_CD IN ( WITH RECURSIVE USER_ROLELIST(COMPANY_CD, DEPT_CD, OPTION, SYSDEF_CD) AS ( WITH TBL1 AS ( SELECT SYS.COMPANY_CD AS COMPANY_CD ,#{LOGIN_DEPT_CD} AS DEPT_CD ,'Y' ::VARCHAR AS OPTION ,SYS.SYSDEF_CD AS SYSDEF_CD ,'005' ::VARCHAR AS PERMISSIONS FROM ES_SYSTEM SYS WHERE SYS.COMPANY_CD = #{COMPANY_CD} AND SYS.FIELD_CD = 'ES_Q0133' ), TBL2 AS ( SELECT A.COMPANY_CD AS COMPANY_CD ,A.DEPT_CD::VARCHAR AS DEPT_CD ,A.OPTION AS OPTION ,SYS.SYSDEF_CD AS SYSDEF_CD ,A.PERMISSIONS AS PERMISSIONS FROM ES_DEPT_CONNECT_USER A LEFT JOIN ES_SYSTEM SYS ON A.COMPANY_CD = SYS.COMPANY_CD AND SYS.FIELD_CD = 'ES_Q0133' WHERE A.COMPANY_CD = #{COMPANY_CD} AND A.USER_ID = #{LOGIN_USER_ID} AND A.DEPT_CD != #{LOGIN_DEPT_CD} ) SELECT COMPANY_CD AS COMPANY_CD, DEPT_CD AS DEPT_CD, OPTION AS "OPTION", SYSDEF_CD AS SYSDEF_CD, PERMISSIONS AS PERMISSIONS FROM TBL1 UNION SELECT COMPANY_CD AS COMPANY_CD, DEPT_CD AS DEPT_CD, OPTION AS "OPTION", SYSDEF_CD AS SYSDEF_CD, PERMISSIONS AS PERMISSIONS FROM TBL2 UNION ALL SELECT A.COMPANY_CD AS COMPANY_CD ,B.DEPT_CD AS DEPT_CD ,A.OPTION AS OPTION ,SYS.SYSDEF_CD AS SYSDEF_CD ,A.PERMISSIONS AS PERMISSIONS FROM USER_ROLELIST A INNER JOIN ES_DEPT B ON A.COMPANY_CD = B.COMPANY_CD AND A.DEPT_CD = B.DEPT_PARENT LEFT JOIN ES_SYSTEM SYS ON A. COMPANY_CD = SYS.COMPANY_CD AND SYS.FIELD_CD = 'ES_Q0133' WHERE SYS.SYSDEF_CD = '01' AND A.OPTION = 'Y' ) SELECT DEPT_CD AS "DEPT_CD" FROM USER_ROLELIST WHERE PERMISSIONS IN (COALESCE(#{PERMISSIONS}, '001'), '005') ORDER BY DEPT_CD )
OR A.DEPT_CD IN
#{item}
)
ORDER BY A.DEPT_CD

/*CommonHelpMapper.HELP_CODE*/ SELECT A.FIELD_CD AS "FIELD_CD" , A.SYSDEF_CD AS "SYSDEF_CD" , A.SYSDEF_NM AS "SYSDEF_NM" , A.SYSCODE_FG1 AS "SYSCODE_FG1" , A.USE_YN AS "USE_YN" FROM ES_CODEDTL A WHERE A.COMPANY_CD = #{COMPANY_CD} AND A.FIELD_CD = #{FIELD_CD}
AND ( A.SYSDEF_CD LIKE '%' || #{KEYWORD} || '%' OR A.SYSDEF_NM LIKE '%' || #{KEYWORD} || '%' )
ORDER BY A.SYSDEF_CD

/*CommonHelpMapper.HELP_CODEDTL*/
SELECT  A.COMPANY_CD  AS "COMPANY_CD"
	 , A.SYSDEF_CD   AS "SYSDEF_CD"
	 , A.SYSDEF_NM   AS "SYSDEF_NM"
	 , A.FLAG1_CD    AS "FLAG1_CD"
	 , A.FLAG2_CD    AS "FLAG2_CD"
	 , A.FLAG3_CD    AS "FLAG3_CD"
	 , A.FLAG4_CD    AS "FLAG4_CD"
FROM   ES_CODEDTL    A
WHERE  A.COMPANY_CD = #{COMPANY_CD}
AND    A.FIELD_CD   = #{FIELD_CD}
AND    A.USE_YN = 'Y'

AND    A.FLAG1_CD = #{FLAG1_CD}


AND    A.FLAG2_CD = #{FLAG2_CD}


AND    A.FLAG3_CD = #{FLAG3_CD}


AND    A.FLAG4_CD = #{FLAG4_CD}


AND    
	(  A.SYSDEF_CD LIKE  '%' || #{KEYWORD} || '%'
	OR A.SYSDEF_NM LIKE  '%' || #{KEYWORD} || '%'
	)
