mapper.select:
/*FileMapper.search*/ SELECT COMPANY_CD AS "COMPANY_CD" , TABLE_ID AS "TABLE_ID" , TABLE_KEY AS "TABLE_KEY" , FILE_SEQ AS "FILE_SEQ" , FILE_NAME AS "FILE_NAME" , ORGN_FILE_NAME AS "ORGN_FILE_NAME" , FILE_PATH AS "FILE_PATH" , FILE_EXT AS "FILE_EXT" , FILE_BYTE AS "FILE_BYTE" , FILE_SIZE AS "FILE_SIZE" , FILE_DIVISION AS "FILE_DIVISION" , MAIN_YN AS "MAIN_YN" , INSERT_ID AS "INSERT_ID" , INSERT_DTS AS "INSERT_DTS" , UPDATE_ID AS "UPDATE_ID" , UPDATE_DTS AS "UPDATE_DTS" FROM ES_FILE WHERE COMPANY_CD = #{COMPANY_CD} AND TABLE_ID = #{TABLE_ID}
AND TABLE_KEY = #{TABLE_KEY}
AND FILE_SEQ = #{FILE_SEQ}
AND FILE_NAME = #{FILE_NAME}

mapper.insert:
/*FileMapper.insert*/
INSERT INTO ES_FILE
(
  COMPANY_CD    
, TABLE_ID      
, TABLE_KEY     
, FILE_SEQ      
, FILE_NAME     
, ORGN_FILE_NAME
, FILE_PATH     
, FILE_EXT      
, FILE_BYTE     
, FILE_SIZE
, FILE_DIVISION     
, MAIN_YN
, INSERT_ID
, INSERT_DTS    
, UPDATE_ID     
, UPDATE_DTS
, LINK
, REMARK
)
VALUES
(
  #{COMPANY_CD}    
, #{TABLE_ID}      
, #{TABLE_KEY}     
, #{FILE_SEQ}      
, #{FILE_NAME}     
, #{ORGN_FILE_NAME}
, #{FILE_PATH}     
, #{FILE_EXT}      
, #{FILE_BYTE}     
, #{FILE_SIZE} 
, #{FILE_DIVISION}   
, #{MAIN_YN}
, #{INSERT_ID}
, #{INSERT_DTS}    
, #{UPDATE_ID}     
, #{UPDATE_DTS}
, #{LINK}
, #{REMARK}
)

mapper.delete:
/*FileMapper.delete*/
DELETE FROM ES_FILE
WHERE COMPANY_CD = #{COMPANY_CD}
AND   TABLE_ID   = #{TABLE_ID}
AND   TABLE_KEY  = cast(#{TABLE_KEY} as text)
AND   FILE_SEQ   = #{FILE_SEQ}

mapper.update:
/*FileMapper.updated*/
update ES_FILE
SET MAIN_YN      =   #{MAIN_YN}
  ,	UPDATE_ID 	 =   #{UPDATE_ID}
  ,	UPDATE_DTS 	 =   #{UPDATE_DTS}
WHERE COMPANY_CD = #{COMPANY_CD}
  AND   TABLE_ID   = #{TABLE_ID}
  AND   FILE_SEQ   = #{FILE_SEQ}
  AND   TABLE_KEY  = cast(#{TABLE_KEY} as text)
  AND   FILE_SEQ   = #{FILE_SEQ}
