mapper.insert:
/*blurb02Mapper.created*/
INSERT INTO ES_BLURB_PKG_M
(
	COMPANY_CD
, PKG_CD
, PKG_NM
, USE_YN
, CREATE_DT
, INSERT_ID
, INSERT_DTS
, UPDATE_ID
, UPDATE_DTS
)
VALUES
(
	#{COMPANY_CD}
, #{PKG_CD}
, #{PKG_NM}
, #{USE_YN}
, #{CREATE_DT}
, #{INSERT_ID}
, #{INSERT_DTS}
, #{UPDATE_ID}
, #{UPDATE_DTS}
)

mapper.update:
UPDATE ES_BLURB_PKG_M
SET USE_YN = #{USE_YN}
  ,	PKG_NM = #{PKG_NM}
  ,	UPDATE_ID = #{UPDATE_ID}
  ,	UPDATE_DTS = #{UPDATE_DTS}
WHERE COMPANY_CD = #{COMPANY_CD}
  AND   PKG_CD = #{PKG_CD}
  AND   CREATE_DT = #{CREATE_DT}

mapper.delete:
DELETE FROM ES_BLURB_PKG_M
WHERE COMPANY_CD = #{COMPANY_CD}
  AND   PKG_CD = #{PKG_CD}
