mapper.select:
/*WebNotice01Mapper.select*/ SELECT COMPANY_CD AS "COMPANY_CD" , BOARD_TYPE AS "BOARD_TYPE" , SEQ AS "SEQ" , TITLE AS "TITLE" , CONTENTS AS "CONTENTS" , MAIN_YN AS "MAIN_YN" FROM ES_BOARD WHERE COMPANY_CD = #{COMPANY_CD}
AND ( TITLE LIKE '%' || #{KEYWORD} || '%' OR CONTENTS LIKE '%' || #{KEYWORD} || '%' )
AND BOARD_TYPE = #{BOARD_TYPE}
AND SEQ = #{SEQ}
ORDER BY SEQ

mapper.insert:
INSERT INTO ES_BOARD
(
  COMPANY_CD
, BOARD_TYPE
, SEQ
, TITLE
, CONTENTS
, HIT
, MAIN_YN
, INSERT_ID
, INSERT_DTS
, UPDATE_ID
, UPDATE_DTS
)
VALUES
(
  #{COMPANY_CD}
, #{BOARD_TYPE}
, (SELECT COALESCE(MAX(SEQ), 0) + 1
   FROM ES_BOARD
   WHERE COMPANY_CD = #{COMPANY_CD}
)
, #{TITLE}
, #{CONTENTS}
, 0
, #{MAIN_YN}
, #{INSERT_ID}
, #{INSERT_DTS}
, #{UPDATE_ID}
, #{UPDATE_DTS}
)

mapper.update:
UPDATE ES_BOARD
SET TITLE      =   #{TITLE}
  ,	CONTENTS   =   #{CONTENTS}
  ,	MAIN_YN   =   #{MAIN_YN}
  ,	BOARD_TYPE   =   #{BOARD_TYPE}
  ,	UPDATE_ID  =   #{UPDATE_ID}
  ,	UPDATE_DTS =   #{UPDATE_DTS}
WHERE COMPANY_CD = #{COMPANY_CD}
  AND   BOARD_TYPE = #{BOARD_TYPE}
  AND   SEQ        = #{SEQ}

mapper.delete:
DELETE FROM ES_BOARD
WHERE COMPANY_CD = #{COMPANY_CD}
  AND   SEQ        = #{SEQ}
