mapper.select:
/*reviewsMapper.reviewsHeader*/
SELECT A.COMPANY_CD AS "COMPANY_CD" , A.PARTNER_CD AS "PARTNER_CD" , A.PARTNER_NM AS "PARTNER_NM" , A.COMPANY_NO AS "COMPANY_NO" , A.CEO_NM AS "CEO_NM" , A.JOB_FIELD AS "JOB_FIELD" , F_ES_CATEGORY_NM(A.COMPANY_CD, A.JOB_FIELD) AS "JOB_FIELD_NM" , A.JOB_EP AS "JOB_EP" , F_ES_CATEGORY_NM(A.COMPANY_CD, A.JOB_EP) AS "JOB_EP_NM" , A.KAKAOTALK AS "KAKAOTALK" FROM ES_PARTNER A WHERE A.COMPANY_CD = #{COMPANY_CD}
AND ( A.PARTNER_CD LIKE '%' || #{KEYWORD} || '%' OR A.PARTNER_NM LIKE '%' || #{KEYWORD} || '%' )
AND A.PARTNER_CD = #{PARTNER_CD}

mapper.insert:
/*reviewsMapper.reviewsDcreated*/
INSERT INTO ES_REVIEW_WRITE
(
    COMPANY_CD
,	PARTNER_CD
,	NAME
,	IP
,	KAKAO_ID
,	SEQ
,	WRITE_DT
,	STAR_SCORE
,	RMK_DC
,	INSERT_ID
,	INSERT_DTS
,	UPDATE_ID
,	UPDATE_DTS
)
VALUES
(
    #{COMPANY_CD}
,   #{PARTNER_CD}
,   #{NAME}
,   #{IP}
,   #{KAKAO_ID}
, (
    SELECT COALESCE(MAX(SEQ), 0) + 1
    FROM ES_REVIEW_WRITE
    WHERE COMPANY_CD = #{COMPANY_CD}
    AND PARTNER_CD = #{PARTNER_CD}
    AND NAME = #{NAME}
    AND NAME = #{IP}
    AND NAME = #{KAKAO_ID}
   )
,   #{WRITE_DT}
,   #{STAR_SCORE}
,   #{RMK_DC}
,   #{INSERT_ID}
,   #{INSERT_DTS}
,   #{UPDATE_ID}
,   #{UPDATE_DTS}
)

mapper.update:
/*reviewsMapper.reviewsDupdated*/
UPDATE ES_REVIEW_WRITE
SET WRITE_DT = #{WRITE_DT}
  ,	STAR_SCORE = #{STAR_SCORE}
  ,	RMK_DC = #{RMK_DC}
  ,	UPDATE_ID = #{UPDATE_ID}
  ,	UPDATE_DTS = #{UPDATE_DTS}
WHERE COMPANY_CD = #{COMPANY_CD}
  AND   PARTNER_CD = #{PARTNER_CD}
  AND   NAME = #{NAME}
  AND   IP = #{IP}
  AND   KAKAO_ID = #{KAKAO_ID}
  AND   SEQ = #{SEQ}

mapper.delete:
/*reviewsMapper.reviewsDdeleted*/
DELETE FROM ES_REVIEW_WRITE
WHERE COMPANY_CD = #{COMPANY_CD}
  AND   PARTNER_CD = #{PARTNER_CD}
  AND   NAME = #{NAME}
  AND   IP = #{IP}
  AND   KAKAO_ID = #{KAKAO_ID}
  AND   SEQ = #{SEQ}
