mapper.select:
        /*V2CommunityMapper.detail*/
        SELECT    SEQ       AS "SEQ"
                , TITLE     AS "TITLE"
                , CONTENTS  AS "CONTENTS"
                , TO_CHAR(TO_TIMESTAMP(INSERT_DTS, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') AS "INSERT_DTS"
--                 , TO_CHAR(TO_TIMESTAMP(INSERT_DTS, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS')
        FROM ES_COMMUNITY
        WHERE COMPANY_CD = #{COMPANY_CD}
        AND SEQ = #{SEQ}

mapper.insert:
/*V2CommunityMapper.createLink*/



                INSERT INTO ES_COMMUNITY_LINK (
                      COMPANY_CD
                    , COMMUNITY_SEQ
                    , SEQ
                    , LINK
                    , INSERT_ID
                    , INSERT_DTS
                ) VALUES (
                      #{COMPANY_CD}
                    , #{SEQ}
                    , #{idx} + 1
                    , #{link}
                    , #{INSERT_ID}
                    , #{INSERT_DTS}
                )

mapper.update:
/*V2CommunityMapper.hitPlus*/
UPDATE ES_COMMUNITY
SET   HIT = (SELECT COALESCE(HIT, 0) + 1 FROM ES_COMMUNITY WHERE COMPANY_CD = #{COMPANY_CD} AND SEQ = #{SEQ})
WHERE  COMPANY_CD    = #{COMPANY_CD}
  AND    SEQ = #{SEQ}
