	, #{NICK_NM}
	, #{PASSWORD}
	, #{CONTENTS}
	, 'Y'
	, #{REPORT_YN}
	, #{WRITE_DATE}
	, #{WRITE_IP}
	, #{INSERT_DATE}
)
