mapper.insert:
/*V2DmMapper.create*/
INSERT INTO PL_DM_M (
      DM_CD
    , DM_TYPE
    , DM_KIND
    , COMP_NM
    , BORW_SITE
    , DEBTOR_TEL
    , DEBTOR_KAKAO
    , DEBTOR_TELE
    , DEBTOR_SNS
    , WITHDR_LOCA
    , COMPL_POLICE
    , DM_CONTENTS
    , PW
    , USE_YN
    , WRITE_DATE
    , WRITE_IP
    , INSERT_DATE
    , UPDATE_DATE
) VALUES (
      #{DM_CD}
    , #{DM_TYPE}
    , #{DM_KIND}
    , #{COMP_NM}
    , #{BORW_SITE}
    , #{DEBTOR_TEL}
    , #{DEBTOR_KAKAO}
    , #{DEBTOR_TELE}
    , #{DEBTOR_SNS}
    , #{WITHDR_LOCA_ARR}
    , #{COMPL_POLICE}
    , #{DM_CONTENTS}
    , #{PW}
    , 'Y'
    , #{WRITE_DATE}
    , #{WRITE_IP}
    , #{INSERT_DATE}
    , #{UPDATE_DATE}
)

/*V2DmMapper.createDeposit*/


        INSERT INTO PL_DM_DEPOSIT (
              DM_CD
            , SEQ
            , BANK_CD
            , NO_DEPOSIT
            , NM_DEPOSITOR
            , USE_YN
            , INSERT_DATE
            , UPDATE_DATE
        ) VALUES (
              #{DM_CD}
            , #{index} + 1
            , SPLIT_PART(#{BANK_CD_ARR}, '|', #{index} + 1) -- BANK_CD
            , SPLIT_PART(#{NO_DEPOSIT_ARR}, '|', #{index} + 1) -- NO_DEPOSIT
            , SPLIT_PART(#{NM_DEPOSITOR_ARR}, '|', #{index} + 1) -- NM_DEPOSITOR
            , 'Y'
            , #{INSERT_DATE}
            , #{UPDATE_DATE}
        )

mapper.select:
/*V2DmMapper.detailDeposit*/
SELECT F_ES_CODE_DETAIL('1000', 'ES_Q0009', BANK_CD) || ' ' || NO_DEPOSIT || ' (' || NM_DEPOSITOR || ')' AS "DEPOSIT"
FROM PL_DM_DEPOSIT
WHERE DM_CD = #{DM_CD}
